import { useEffect, useState } from "react";
import { useAuth } from "@/contexts/AuthContext";
import { useLanguage } from "@/contexts/LanguageContext";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Progress } from "@/components/ui/progress";
import { Plus, Minus, Target } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { ReadingGoal } from "@shared/schema";

export default function Reading() {
  const { user } = useAuth();
  const { t, language, dir } = useLanguage();
  const { toast } = useToast();
  const [goal, setGoal] = useState<ReadingGoal>({
    id: "",
    userId: user?.id || "",
    date: new Date().toISOString().split("T")[0],
    target: 10,
    progress: 0,
  });

  useEffect(() => {
    const todayDate = new Date().toISOString().split("T")[0];
    const stored = localStorage.getItem(`reading_${user?.id}_${todayDate}`);
    if (stored) {
      setGoal(JSON.parse(stored));
    } else {
      const newGoal: ReadingGoal = {
        id: crypto.randomUUID(),
        userId: user?.id || "",
        date: todayDate,
        target: 10,
        progress: 0,
      };
      setGoal(newGoal);
    }
  }, [user]);

  const updateProgress = (delta: number) => {
    const newProgress = Math.max(0, Math.min(goal.progress + delta, goal.target));
    const updated = { ...goal, progress: newProgress };
    setGoal(updated);
    localStorage.setItem(`reading_${user?.id}_${updated.date}`, JSON.stringify(updated));

    if (newProgress === goal.target && goal.progress < goal.target) {
      toast({
        title: language === "ar" ? "ما شاء الله!" : "MashaAllah!",
        description: language === "ar" ? "لقد أكملت هدف القراءة اليومي" : "You've completed your daily reading goal",
      });
    }
  };

  const updateTarget = (newTarget: number) => {
    if (newTarget < 1) return;
    const updated = { ...goal, target: newTarget };
    setGoal(updated);
    localStorage.setItem(`reading_${user?.id}_${updated.date}`, JSON.stringify(updated));
  };

  const progressPercentage = (goal.progress / goal.target) * 100;

  return (
    <div className="p-8 space-y-8">
      <h1 className="text-4xl font-bold">{t("reading")}</h1>

      <div className="max-w-4xl mx-auto space-y-8">
        <Card className="shadow-xl" data-testid="card-reading-goal">
          <CardHeader>
            <CardTitle className="text-3xl text-center">{t("readingGoal")}</CardTitle>
          </CardHeader>
          <CardContent className="space-y-8">
            <div className="text-center space-y-4">
              <div className="text-8xl font-bold text-primary">
                <span data-testid="text-progress">{goal.progress}</span>
                <span className="text-4xl text-muted-foreground">/{goal.target}</span>
              </div>
              <p className="text-lg text-muted-foreground">
                {language === "ar" ? "صفحات اليوم" : "Pages Today"}
              </p>
            </div>

            <div className="space-y-2">
              <Progress value={progressPercentage} className="h-4" />
              <p className="text-center text-sm text-muted-foreground">
                {Math.round(progressPercentage)}% {language === "ar" ? "مكتمل" : "Complete"}
              </p>
            </div>

            <div className={`flex justify-center gap-4 ${dir === "rtl" ? "flex-row-reverse" : ""}`}>
              <Button
                variant="outline"
                size="lg"
                onClick={() => updateProgress(-1)}
                disabled={goal.progress === 0}
                className="h-16 w-16 rounded-full"
                data-testid="button-decrement"
              >
                <Minus className="h-8 w-8" />
              </Button>
              <Button
                variant="default"
                size="lg"
                onClick={() => updateProgress(1)}
                disabled={goal.progress >= goal.target}
                className="h-16 w-16 rounded-full"
                data-testid="button-increment"
              >
                <Plus className="h-8 w-8" />
              </Button>
            </div>

            {goal.progress >= goal.target && (
              <div className="text-center p-6 bg-primary/10 rounded-lg animate-pulse">
                <p className="text-2xl font-bold text-primary">
                  {language === "ar" ? "ما شاء الله! تم إنجاز الهدف اليومي" : "MashaAllah! Daily Goal Achieved"}
                </p>
              </div>
            )}
          </CardContent>
        </Card>

        <Card className="shadow-lg">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Target className="h-6 w-6" />
              {language === "ar" ? "تعيين الهدف" : "Set Goal"}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label>{language === "ar" ? "الهدف اليومي (صفحات)" : "Daily Target (pages)"}</Label>
              <Input
                type="number"
                value={goal.target}
                onChange={(e) => updateTarget(Number(e.target.value))}
                min={1}
                data-testid="input-target"
              />
            </div>
            <p className="text-sm text-muted-foreground">
              {language === "ar"
                ? "حدد عدد الصفحات التي تريد قراءتها يومياً"
                : "Set the number of pages you want to read daily"}
            </p>
          </CardContent>
        </Card>

        <Card className="shadow-lg bg-gradient-to-br from-primary/5 to-primary/10 border-primary/20">
          <CardHeader>
            <CardTitle>{language === "ar" ? "نصائح للقراءة" : "Reading Tips"}</CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            <ul className="space-y-2 text-muted-foreground">
              <li className="flex items-start gap-2">
                <span className="text-primary font-bold">•</span>
                <span>{language === "ar" ? "اقرأ في أوقات ثابتة يومياً" : "Read at consistent times daily"}</span>
              </li>
              <li className="flex items-start gap-2">
                <span className="text-primary font-bold">•</span>
                <span>{language === "ar" ? "ابدأ بأهداف صغيرة وزدها تدريجياً" : "Start with small goals and increase gradually"}</span>
              </li>
              <li className="flex items-start gap-2">
                <span className="text-primary font-bold">•</span>
                <span>{language === "ar" ? "خصص مكاناً هادئاً للقراءة" : "Designate a quiet reading space"}</span>
              </li>
              <li className="flex items-start gap-2">
                <span className="text-primary font-bold">•</span>
                <span>{language === "ar" ? "دون ملاحظاتك وتأملاتك" : "Take notes and reflect on what you read"}</span>
              </li>
            </ul>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/worship.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/contexts/AuthContext";
import { useLanguage } from "@/contexts/LanguageContext";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Button } from "@/components/ui/button";
import { DhikrCounter } from "@/components/DhikrCounter";
import { MapPin, Loader2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { PrayerTimes } from "@shared/schema";

export default function Worship() {
  const { user } = useAuth();
  const { t, language } = useLanguage();
  const { toast } = useToast();
  const [prayerTimes, setPrayerTimes] = useState<PrayerTimes | null>(null);
  const [loading, setLoading] = useState(false);
  const [prayers, setPrayers] = useState({
    fajr: false,
    dhuhr: false,
    asr: false,
    maghrib: false,
    isha: false,
    sunnahFajr: false,
    sunnahDhuhr: false,
    sunnahAsr: false,
    sunnahMaghrib: false,
    sunnahIsha: false,
  });

  const fetchPrayerTimes = async () => {
    setLoading(true);
    try {
      const position = await new Promise<GeolocationPosition>((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject);
      });

      const { latitude, longitude } = position.coords;
      const date = new Date();
      const response = await fetch(
        `https://api.aladhan.com/v1/timings/${date.getTime()}?latitude=${latitude}&longitude=${longitude}&method=2`
      );
      const data = await response.json();

      if (data.code === 200) {
        const times: PrayerTimes = {
          fajr: data.data.timings.Fajr,
          dhuhr: data.data.timings.Dhuhr,
          asr: data.data.timings.Asr,
          maghrib: data.data.timings.Maghrib,
          isha: data.data.timings.Isha,
          sunrise: data.data.timings.Sunrise,
        };
        setPrayerTimes(times);
        localStorage.setItem("prayerTimes", JSON.stringify(times));
        localStorage.setItem("prayerTimesDate", new Date().toISOString().split("T")[0]);
        toast({
          title: language === "ar" ? "تم جلب أوقات الصلاة" : "Prayer times fetched",
          description: language === "ar" ? "تم تحديث أوقات الصلاة بنجاح" : "Prayer times updated successfully",
        });
      }
    } catch (error) {
      toast({
        title: language === "ar" ? "خطأ" : "Error",
        description: language === "ar" ? "فشل في جلب أوقات الصلاة" : "Failed to fetch prayer times",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const todayDate = new Date().toISOString().split("T")[0];
    const stored = localStorage.getItem(`prayers_${user?.id}_${todayDate}`);
    if (stored) {
      setPrayers(JSON.parse(stored));
    }

    const storedTimes = localStorage.getItem("prayerTimes");
    const storedDate = localStorage.getItem("prayerTimesDate");
    if (storedTimes && storedDate === todayDate) {
      setPrayerTimes(JSON.parse(storedTimes));
    } else {
      fetchPrayerTimes();
    }
  }, [user]);

  const togglePrayer = (prayer: keyof typeof prayers) => {
    const newPrayers = { ...prayers, [prayer]: !prayers[prayer] };
    setPrayers(newPrayers);
    const todayDate = new Date().toISOString().split("T")[0];
    localStorage.setItem(`prayers_${user?.id}_${todayDate}`, JSON.stringify(newPrayers));
  };

  const prayerNames = [
    { key: "fajr", name: t("fajr"), time: prayerTimes?.fajr, testId: "prayer-fajr" },
    { key: "dhuhr", name: t("dhuhr"), time: prayerTimes?.dhuhr, testId: "prayer-dhuhr" },
    { key: "asr", name: t("asr"), time: prayerTimes?.asr, testId: "prayer-asr" },
    { key: "maghrib", name: t("maghrib"), time: prayerTimes?.maghrib, testId: "prayer-maghrib" },
    { key: "isha", name: t("isha"), time: prayerTimes?.isha, testId: "prayer-isha" },
  ];

  return (
    <div className="p-8 space-y-8">
      <div className="flex items-center justify-between">
        <h1 className="text-4xl font-bold">{t("worship")}</h1>
        <Button onClick={fetchPrayerTimes} disabled={loading} data-testid="button-fetch-times">
          {loading ? <Loader2 className="h-4 w-4 animate-spin" /> : <MapPin className="h-4 w-4" />}
          <span className="ml-2">{language === "ar" ? "جلب أوقات الصلاة" : "Fetch Prayer Times"}</span>
        </Button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {prayerNames.map((prayer) => (
          <Card key={prayer.key} className="shadow-lg" data-testid={prayer.testId}>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <span>{prayer.name}</span>
                {prayer.time && <span className="text-2xl font-mono">{prayer.time}</span>}
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex items-center gap-3">
                <Checkbox
                  id={prayer.key}
                  checked={prayers[prayer.key as keyof typeof prayers]}
                  onCheckedChange={() => togglePrayer(prayer.key as keyof typeof prayers)}
                  data-testid={`checkbox-${prayer.key}`}
                />
                <label htmlFor={prayer.key} className="text-lg font-medium cursor-pointer">
                  {language === "ar" ? "الفريضة" : "Obligatory"}
                </label>
              </div>
              <div className="flex items-center gap-3">
                <Checkbox
                  id={`sunnah${prayer.key}`}
                  checked={prayers[`sunnah${prayer.key.charAt(0).toUpperCase() + prayer.key.slice(1)}` as keyof typeof prayers]}
                  onCheckedChange={() => togglePrayer(`sunnah${prayer.key.charAt(0).toUpperCase() + prayer.key.slice(1)}` as keyof typeof prayers)}
                  data-testid={`checkbox-sunnah-${prayer.key}`}
                />
                <label htmlFor={`sunnah${prayer.key}`} className="text-lg text-muted-foreground cursor-pointer">
                  {t("sunnah")}
                </label>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      <div className="max-w-2xl mx-auto">
        <DhikrCounter />
      </div>
    </div>
  );
}

