import * as React from "react"
import useEmblaCarousel, {
  type UseEmblaCarouselType,
} from "embla-carousel-react"
import { ArrowLeft, ArrowRight } from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"

type CarouselApi = UseEmblaCarouselType[1]
type UseCarouselParameters = Parameters<typeof useEmblaCarousel>
type CarouselOptions = UseCarouselParameters[0]
type CarouselPlugin = UseCarouselParameters[1]

type CarouselProps = {
  opts?: CarouselOptions
  plugins?: CarouselPlugin
  orientation?: "horizontal" | "vertical"
  setApi?: (api: CarouselApi) => void
}

type CarouselContextProps = {
  carouselRef: ReturnType<typeof useEmblaCarousel>[0]
  api: ReturnType<typeof useEmblaCarousel>[1]
  scrollPrev: () => void
  scrollNext: () => void
  canScrollPrev: boolean
  canScrollNext: boolean
} & CarouselProps

const CarouselContext = React.createContext<CarouselContextProps | null>(null)

function useCarousel() {
  const context = React.useContext(CarouselContext)

  if (!context) {
    throw new Error("useCarousel must be used within a <Carousel />")
  }

  return context
}

const Carousel = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement> & CarouselProps
>(
  (
    {
      orientation = "horizontal",
      opts,
      setApi,
      plugins,
      className,
      children,
      ...props
    },
    ref
  ) => {
    const [carouselRef, api] = useEmblaCarousel(
      {
        ...opts,
        axis: orientation === "horizontal" ? "x" : "y",
      },
      plugins
    )
    const [canScrollPrev, setCanScrollPrev] = React.useState(false)
    const [canScrollNext, setCanScrollNext] = React.useState(false)

    const onSelect = React.useCallback((api: CarouselApi) => {
      if (!api) {
        return
      }

      setCanScrollPrev(api.canScrollPrev())
      setCanScrollNext(api.canScrollNext())
    }, [])

    const scrollPrev = React.useCallback(() => {
      api?.scrollPrev()
    }, [api])

    const scrollNext = React.useCallback(() => {
      api?.scrollNext()
    }, [api])

    const handleKeyDown = React.useCallback(
      (event: React.KeyboardEvent<HTMLDivElement>) => {
        if (event.key === "ArrowLeft") {
          event.preventDefault()
          scrollPrev()
        } else if (event.key === "ArrowRight") {
          event.preventDefault()
          scrollNext()
        }
      },
      [scrollPrev, scrollNext]
    )

    React.useEffect(() => {
      if (!api || !setApi) {
        return
      }

      setApi(api)
    }, [api, setApi])

    React.useEffect(() => {
      if (!api) {
        return
      }

      onSelect(api)
      api.on("reInit", onSelect)
      api.on("select", onSelect)

      return () => {
        api?.off("select", onSelect)
      }
    }, [api, onSelect])

    return (
      <CarouselContext.Provider
        value={{
          carouselRef,
          api: api,
          opts,
          orientation:
            orientation || (opts?.axis === "y" ? "vertical" : "horizontal"),
          scrollPrev,
          scrollNext,
          canScrollPrev,
          canScrollNext,
        }}
      >
        <div
          ref={ref}
          onKeyDownCapture={handleKeyDown}
          className={cn("relative", className)}
          role="region"
          aria-roledescription="carousel"
          {...props}
        >
          {children}
        </div>
      </CarouselContext.Provider>
    )
  }
)
Carousel.displayName = "Carousel"

const CarouselContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { carouselRef, orientation } = useCarousel()

  return (
    <div ref={carouselRef} className="overflow-hidden">
      <div
        ref={ref}
        className={cn(
          "flex",
          orientation === "horizontal" ? "-ml-4" : "-mt-4 flex-col",
          className
        )}
        {...props}
      />
    </div>
  )
})
CarouselContent.displayName = "CarouselContent"

const CarouselItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { orientation } = useCarousel()

  return (
    <div
      ref={ref}
      role="group"
      aria-roledescription="slide"
      className={cn(
        "min-w-0 shrink-0 grow-0 basis-full",
        orientation === "horizontal" ? "pl-4" : "pt-4",
        className
      )}
      {...props}
    />
  )
})
CarouselItem.displayName = "CarouselItem"

const CarouselPrevious = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollPrev, canScrollPrev } = useCarousel()

  return (
    <Button
      ref={ref}
      variant={variant}
      size={size}
      className={cn(
        "absolute  h-8 w-8 rounded-full",
        orientation === "horizontal"
          ? "-left-12 top-1/2 -translate-y-1/2"
          : "-top-12 left-1/2 -translate-x-1/2 rotate-90",
        className
      )}
      disabled={!canScrollPrev}
      onClick={scrollPrev}
      {...props}
    >
      <ArrowLeft className="h-4 w-4" />
      <span className="sr-only">Previous slide</span>
    </Button>
  )
})
CarouselPrevious.displayName = "CarouselPrevious"

const CarouselNext = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollNext, canScrollNext } = useCarousel()

  return (
    <Button
      ref={ref}
      variant={variant}
      size={size}
      className={cn(
        "absolute h-8 w-8 rounded-full",
        orientation === "horizontal"
          ? "-right-12 top-1/2 -translate-y-1/2"
          : "-bottom-12 left-1/2 -translate-x-1/2 rotate-90",
        className
      )}
      disabled={!canScrollNext}
      onClick={scrollNext}
      {...props}
    >
      <ArrowRight className="h-4 w-4" />
      <span className="sr-only">Next slide</span>
    </Button>
  )
})
CarouselNext.displayName = "CarouselNext"

export {
  type CarouselApi,
  Carousel,
  CarouselContent,
  CarouselItem,
  CarouselPrevious,
  CarouselNext,
}

client/src/pages/tasks.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/contexts/AuthContext";
import { useLanguage } from "@/contexts/LanguageContext";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Plus, Trash2, Edit } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { Task } from "@shared/schema";

export default function Tasks() {
  const { user } = useAuth();
  const { t, language } = useLanguage();
  const { toast } = useToast();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [editingTask, setEditingTask] = useState<Task | null>(null);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    priority: "medium" as "low" | "medium" | "high",
    tags: "",
  });
  const [filter, setFilter] = useState<"all" | "completed" | "pending">("all");

  useEffect(() => {
    const stored = localStorage.getItem(`tasks_${user?.id}`);
    if (stored) {
      setTasks(JSON.parse(stored));
    }
  }, [user]);

  const saveTask = () => {
    if (!formData.title.trim()) return;

    const tagsArray = formData.tags.split(",").map((t) => t.trim()).filter(Boolean);

    if (editingTask) {
      const updated = tasks.map((t) =>
        t.id === editingTask.id
          ? { ...t, ...formData, tags: tagsArray }
          : t
      );
      setTasks(updated);
      localStorage.setItem(`tasks_${user?.id}`, JSON.stringify(updated));
      toast({
        title: language === "ar" ? "تم التحديث" : "Updated",
        description: language === "ar" ? "تم تحديث المهمة" : "Task updated",
      });
    } else {
      const newTask: Task = {
        id: crypto.randomUUID(),
        userId: user?.id || "",
        title: formData.title,
        description: formData.description,
        priority: formData.priority,
        tags: tagsArray,
        completed: false,
        createdAt: new Date().toISOString(),
      };
      const updated = [...tasks, newTask];
      setTasks(updated);
      localStorage.setItem(`tasks_${user?.id}`, JSON.stringify(updated));
      toast({
        title: language === "ar" ? "تم الإضافة" : "Added",
        description: language === "ar" ? "تم إضافة المهمة" : "Task added",
      });
    }

    setFormData({ title: "", description: "", priority: "medium", tags: "" });
    setEditingTask(null);
    setIsAddDialogOpen(false);
  };

  const toggleComplete = (id: string) => {
    const updated = tasks.map((t) =>
      t.id === id ? { ...t, completed: !t.completed } : t
    );
    setTasks(updated);
    localStorage.setItem(`tasks_${user?.id}`, JSON.stringify(updated));
  };

  const deleteTask = (id: string) => {
    const updated = tasks.filter((t) => t.id !== id);
    setTasks(updated);
    localStorage.setItem(`tasks_${user?.id}`, JSON.stringify(updated));
    toast({
      title: language === "ar" ? "تم الحذف" : "Deleted",
      description: language === "ar" ? "تم حذف المهمة" : "Task deleted",
    });
  };

  const startEdit = (task: Task) => {
    setEditingTask(task);
    setFormData({
      title: task.title,
      description: task.description || "",
      priority: task.priority,
      tags: task.tags.join(", "),
    });
    setIsAddDialogOpen(true);
  };

  const filteredTasks = tasks.filter((task) => {
    if (filter === "completed") return task.completed;
    if (filter === "pending") return !task.completed;
    return true;
  });

  const priorityColors = {
    high: "bg-destructive text-destructive-foreground",
    medium: "bg-primary text-primary-foreground",
    low: "bg-muted text-muted-foreground",
  };

  return (
    <div className="p-8 space-y-8">
      <div className="flex items-center justify-between flex-wrap gap-4">
        <h1 className="text-4xl font-bold">{t("tasks")}</h1>
        <div className="flex gap-3 items-center">
          <Select value={filter} onValueChange={(value: any) => setFilter(value)}>
            <SelectTrigger className="w-40" data-testid="select-filter">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">{language === "ar" ? "الكل" : "All"}</SelectItem>
              <SelectItem value="pending">{t("pending")}</SelectItem>
              <SelectItem value="completed">{t("completed")}</SelectItem>
            </SelectContent>
          </Select>
          <Dialog
            open={isAddDialogOpen}
            onOpenChange={(open) => {
              setIsAddDialogOpen(open);
              if (!open) {
                setEditingTask(null);
                setFormData({ title: "", description: "", priority: "medium", tags: "" });
              }
            }}
          >
            <DialogTrigger asChild>
              <Button data-testid="button-add-task">
                <Plus className="h-4 w-4 mr-2" />
                {language === "ar" ? "إضافة مهمة" : "Add Task"}
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>
                  {editingTask
                    ? language === "ar" ? "تعديل المهمة" : "Edit Task"
                    : language === "ar" ? "إضافة مهمة جديدة" : "Add New Task"}
                </DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label>{t("title")}</Label>
                  <Input
                    value={formData.title}
                    onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                    placeholder={language === "ar" ? "عنوان المهمة" : "Task title"}
                    data-testid="input-task-title"
                  />
                </div>
                <div>
                  <Label>{t("description")}</Label>
                  <Textarea
                    value={formData.description}
                    onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                    placeholder={language === "ar" ? "وصف المهمة" : "Task description"}
                    data-testid="input-task-description"
                  />
                </div>
                <div>
                  <Label>{t("priority")}</Label>
                  <Select
                    value={formData.priority}
                    onValueChange={(value: any) => setFormData({ ...formData, priority: value })}
                  >
                    <SelectTrigger data-testid="select-priority">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="high">{t("high")}</SelectItem>
                      <SelectItem value="medium">{t("medium")}</SelectItem>
                      <SelectItem value="low">{t("low")}</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label>{t("tags")}</Label>
                  <Input
                    value={formData.tags}
                    onChange={(e) => setFormData({ ...formData, tags: e.target.value })}
                    placeholder={language === "ar" ? "مثال: عمل, شخصي" : "e.g., work, personal"}
                    data-testid="input-task-tags"
                  />
                </div>
                <Button onClick={saveTask} className="w-full" data-testid="button-save-task">
                  {t("save")}
                </Button>
              </div>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredTasks.length === 0 ? (
          <div className="col-span-full text-center py-16">
            <p className="text-muted-foreground text-lg">
              {language === "ar" ? "لا توجد مهام" : "No tasks found"}
            </p>
          </div>
        ) : (
          filteredTasks.map((task) => (
            <Card
              key={task.id}
              className={`shadow-lg ${task.completed ? "opacity-60" : ""}`}
              data-testid={`task-${task.id}`}
            >
              <CardHeader className="pb-3">
                <div className="flex items-start justify-between gap-2">
                  <div className="flex items-start gap-3 flex-1">
                    <Checkbox
                      checked={task.completed}
                      onCheckedChange={() => toggleComplete(task.id)}
                      className="mt-1"
                      data-testid={`checkbox-task-${task.id}`}
                    />
                    <div className="flex-1">
                      <CardTitle className={`text-lg ${task.completed ? "line-through" : ""}`}>
                        {task.title}
                      </CardTitle>
                    </div>
                  </div>
                  <div className="flex gap-1">
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => startEdit(task)}
                      data-testid={`button-edit-task-${task.id}`}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => deleteTask(task.id)}
                      data-testid={`button-delete-task-${task.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-3">
                {task.description && (
                  <p className="text-sm text-muted-foreground">{task.description}</p>
                )}
                <div className="flex flex-wrap gap-2">
                  <Badge variant="outline" className={priorityColors[task.priority]}>
                    {t(task.priority)}
                  </Badge>
                  {task.tags.map((tag, i) => (
                    <Badge key={i} variant="secondary">
                      {tag}
                    </Badge>
                  ))}
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

client/src/pages/study.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/contexts/AuthContext";
import { useLanguage } from "@/contexts/LanguageContext";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { PomodoroTimer } from "@/components/PomodoroTimer";
import { StudySessionDialog } from "@/components/StudySessionDialog";
import { Plus, Trash2, BookOpen, AlertCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { Course, DailyMetric, StudySessionNotes, Settings } from "@shared/schema";

export default function Study() {
  const { user } = useAuth();
  const { t, language } = useLanguage();
  const { toast } = useToast();
  const [courses, setCourses] = useState<Course[]>([]);
  const [selectedCourse, setSelectedCourse] = useState<Course | null>(null);
  const [dailyMetrics, setDailyMetrics] = useState<DailyMetric>({
    id: "",
    userId: user?.id || "",
    courseId: "",
    date: new Date().toISOString().split("T")[0],
    metric1: 0,
    metric2: 0,
    metric3: 0,
    metric4: 0,
    metric5: 0,
  });
  const [sessionNotes, setSessionNotes] = useState<StudySessionNotes>({
    id: "",
    userId: user?.id || "",
    courseId: "",
    date: new Date().toISOString().split("T")[0],
    errorLog: "",
    reviewNotes: "",
  });
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isSessionDialogOpen, setIsSessionDialogOpen] = useState(false);
  const [courseName, setCourseName] = useState("");
  const [courseColor, setCourseColor] = useState("#D4AF37");
  const [metricNames, setMetricNames] = useState<Settings["metricNames"]>({
    metric1: { en: "Focus", ar: "التركيز", visible: true },
    metric2: { en: "Interaction", ar: "التفاعل", visible: true },
    metric3: { en: "Application", ar: "التطبيق العملي", visible: true },
    metric4: { en: "Mistake Reduction", ar: "تقليل الأخطاء", visible: true },
    metric5: { en: "Discipline", ar: "الانضباط", visible: true },
  });

  useEffect(() => {
    if (!user?.id) return;

    const stored = localStorage.getItem(`courses_${user.id}`);
    if (stored) {
      setCourses(JSON.parse(stored));
    }

    const settingsStored = localStorage.getItem(`settings_${user.id}`);
    if (settingsStored) {
      const settings: Settings = JSON.parse(settingsStored);
      if (settings.metricNames) {
        setMetricNames(settings.metricNames);
      }
    }
  }, [user]);

  useEffect(() => {
    if (selectedCourse && user?.id) {
      const todayDate = new Date().toISOString().split("T")[0];

      const storedMetrics = localStorage.getItem(`metrics_${user.id}_${selectedCourse.id}_${todayDate}`);
      if (storedMetrics) {
        setDailyMetrics(JSON.parse(storedMetrics));
      } else {
        setDailyMetrics({
          id: crypto.randomUUID(),
          userId: user.id,
          courseId: selectedCourse.id,
          date: todayDate,
          metric1: 0,
          metric2: 0,
          metric3: 0,
          metric4: 0,
          metric5: 0,
        });
      }

      const storedNotes = localStorage.getItem(`notes_${user.id}_${selectedCourse.id}_${todayDate}`);
      if (storedNotes) {
        setSessionNotes(JSON.parse(storedNotes));
      } else {
        setSessionNotes({
          id: crypto.randomUUID(),
          userId: user.id,
          courseId: selectedCourse.id,
          date: todayDate,
          errorLog: "",
          reviewNotes: "",
        });
      }
    }
  }, [selectedCourse, user]);

  const addCourse = () => {
    if (!courseName.trim() || !user?.id) return;

    const newCourse: Course = {
      id: crypto.randomUUID(),
      userId: user.id,
      name: courseName,
      color: courseColor,
      createdAt: new Date().toISOString(),
    };

    const updated = [...courses, newCourse];
    setCourses(updated);
    localStorage.setItem(`courses_${user.id}`, JSON.stringify(updated));
    setCourseName("");
    setCourseColor("#D4AF37");
    setIsAddDialogOpen(false);
    toast({
      title: language === "ar" ? "تم الإضافة" : "Added",
      description: language === "ar" ? "تم إضافة المادة بنجاح" : "Course added successfully",
    });
  };

  const deleteCourse = (id: string) => {
    if (!user?.id) return;
    const updated = courses.filter((c) => c.id !== id);
    setCourses(updated);
    localStorage.setItem(`courses_${user.id}`, JSON.stringify(updated));
    if (selectedCourse?.id === id) setSelectedCourse(null);
    toast({
      title: language === "ar" ? "تم الحذف" : "Deleted",
      description: language === "ar" ? "تم حذف المادة" : "Course deleted",
    });
  };

  const updateMetric = (metricKey: keyof DailyMetric, value: number) => {
    if (!user?.id || !selectedCourse?.id) return;
    const updated = { ...dailyMetrics, [metricKey]: value };
    setDailyMetrics(updated);
    localStorage.setItem(`metrics_${user.id}_${selectedCourse.id}_${updated.date}`, JSON.stringify(updated));
  };

  return (
    <div className="p-8 space-y-8">
      <div className="flex items-center justify-between">
        <h1 className="text-4xl font-bold">{t("study")}</h1>
        <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-add-course">
              <Plus className="h-4 w-4 mr-2" />
              {language === "ar" ? "إضافة مادة" : "Add Course"}
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>{language === "ar" ? "إضافة مادة جديدة" : "Add New Course"}</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div>
                <Label>{language === "ar" ? "اسم المادة" : "Course Name"}</Label>
                <Input
                  value={courseName}
                  onChange={(e) => setCourseName(e.target.value)}
                  placeholder={language === "ar" ? "مثال: الرياضيات" : "e.g., Mathematics"}
                  data-testid="input-course-name"
                />
              </div>
              <div>
                <Label>{language === "ar" ? "اللون" : "Color"}</Label>
                <Input
                  type="color"
                  value={courseColor}
                  onChange={(e) => setCourseColor(e.target.value)}
                  data-testid="input-course-color"
                />
              </div>
              <Button onClick={addCourse} className="w-full" data-testid="button-save-course">
                {t("save")}
              </Button>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-1">
          <Card className="shadow-lg">
            <CardHeader>
              <CardTitle>{t("courseManagement")}</CardTitle>
            </CardHeader>
            <CardContent className="space-y-2">
              {courses.length === 0 ? (
                <p className="text-muted-foreground text-center py-8">
                  {language === "ar" ? "لا توجد مواد بعد" : "No courses yet"}
                </p>
              ) : (
                courses.map((course) => (
                  <div
                    key={course.id}
                    className={`p-4 rounded-lg cursor-pointer hover-elevate active-elevate-2 transition-all ${
                      selectedCourse?.id === course.id ? "bg-accent" : "bg-card"
                    }`}
                    onClick={() => setSelectedCourse(course)}
                    data-testid={`course-${course.id}`}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-3">
                        <div
                          className="w-4 h-4 rounded-full"
                          style={{ backgroundColor: course.color }}
                        />
                        <span className="font-medium">{course.name}</span>
                      </div>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={(e) => {
                          e.stopPropagation();
                          deleteCourse(course.id);
                        }}
                        data-testid={`button-delete-course-${course.id}`}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                ))
              )}
            </CardContent>
          </Card>
        </div>

        <div className="lg:col-span-2 space-y-8">
          {selectedCourse && (
            <Card className="shadow-lg">
              <CardHeader>
                <CardTitle>{language === "ar" ? `المقاييس اليومية - ${selectedCourse.name}` : `Daily Metrics - ${selectedCourse.name}`}</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {(['metric1', 'metric2', 'metric3', 'metric4', 'metric5'] as const).map((key) => {
                  const metricConfig = metricNames[key];
                  if (!metricConfig.visible) return null;

                  return (
                    <div key={key} className="space-y-2">
                      <Label>{language === "ar" ? metricConfig.ar : metricConfig.en}</Label>
                      <Input
                        type="number"
                        value={dailyMetrics[key]}
                        onChange={(e) => updateMetric(key, Number(e.target.value))}
                        min={0}
                        max={10}
                        placeholder="0-10"
                        data-testid={`input-${key}`}
                      />
                    </div>
                  );
                })}
              </CardContent>
            </Card>
          )}

          {selectedCourse && (
            <Card className="shadow-lg">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <BookOpen className="h-5 w-5" />
                  {language === "ar" ? "سجل الأخطاء والمراجعة" : "Error Log & Review Notes"}
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <Label className="flex items-center gap-2">
                    <AlertCircle className="h-4 w-4" />
                    {language === "ar" ? "الأخطاء والنقاط المربكة" : "Errors & Confusing Points"}
                  </Label>
                  <Textarea
                    value={sessionNotes.errorLog}
                    onChange={(e) => {
                      if (!user?.id || !selectedCourse?.id) return;
                      const updated = { ...sessionNotes, errorLog: e.target.value };
                      setSessionNotes(updated);
                      localStorage.setItem(`notes_${user.id}_${selectedCourse.id}_${sessionNotes.date}`, JSON.stringify(updated));
                    }}
                    rows={4}
                    placeholder={language === "ar" ? "سجل الأخطاء..." : "Log errors..."}
                    data-testid="textarea-errorlog"
                  />
                </div>
                <div className="space-y-2">
                  <Label>{language === "ar" ? "ملاحظات المراجعة" : "Review Notes"}</Label>
                  <Textarea
                    value={sessionNotes.reviewNotes}
                    onChange={(e) => {
                      if (!user?.id || !selectedCourse?.id) return;
                      const updated = { ...sessionNotes, reviewNotes: e.target.value };
                      setSessionNotes(updated);
                      localStorage.setItem(`notes_${user.id}_${selectedCourse.id}_${sessionNotes.date}`, JSON.stringify(updated));
                    }}
                    rows={4}
                    placeholder={language === "ar" ? "ملاحظات للمراجعة..." : "Notes for review..."}
                    data-testid="textarea-reviewnotes"
                  />
                </div>
              </CardContent>
            </Card>
          )}

          <PomodoroTimer
            onSessionComplete={() => {
              if (!user?.id) return;
              const todayDate = new Date().toISOString().split("T")[0];
              const stored = JSON.parse(localStorage.getItem(`pomodoro_${user.id}_${todayDate}`) || "{}");
              stored.sessionsCompleted = (stored.sessionsCompleted || 0) + 1;
              localStorage.setItem(`pomodoro_${user.id}_${todayDate}`, JSON.stringify(stored));
              setIsSessionDialogOpen(true);
            }}
          />

          <StudySessionDialog
            open={isSessionDialogOpen}
            onClose={() => setIsSessionDialogOpen(false)}
            onSave={(courseId, metrics, notes) => {
              if (selectedCourse?.id === courseId) {
                setDailyMetrics(metrics);
                setSessionNotes(notes);
              }
            }}
            courses={courses}
          />
        </div>
      </div>
    </div>
  );
}

